package WebDriverManager;

public class Printstar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=3;
		String x="";
		String y="";
		
		for(int i=0;i<n;i++)
		{
			if(i!=0)
			{
				x=x+" a";
			}
			if(i==0)
			{
			x+="a";
			}
			y=x+".";
			System.out.println(y);
			
		}
		
		

	}

}
